// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

/**
 * @title AegisProxy
 * @dev This contract implements a proxy that is upgradeable by an admin.
 * This is the core of the Aegis Framework - enabling secure upgrades while maintaining state.
 */
contract AegisProxy is ERC1967Proxy {
    /**
     * @dev Initializes an upgradeable proxy managed by `_admin`.
     * @param _logic Address of the initial implementation.
     * @param _admin Address of the proxy admin.
     * @param _data Data to send as msg.data to the implementation to initialize the proxied contract.
     */
    constructor(
        address _logic,
        address _admin, 
        bytes memory _data
    ) ERC1967Proxy(_logic, _data) {
        _changeAdmin(_admin);
    }

    /**
     * @dev Returns the current implementation address.
     */
    function getImplementation() external view returns (address) {
        return _implementation();
    }

    /**
     * @dev Returns the current admin address.
     */
    function getAdmin() external view returns (address) {
        return _getAdmin();
    }
}
